/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 FUNCTION `fget_docnum`(sdocid INTeger,syop int ,smop int) RETURNS varchar(100) CHARSET latin1
BEGIN
	/*
	select fget_docnum('01',1,2012,7);
	*/
	declare vc_docnum varchar(100) default '';
	DECLARE vc_docnum_awl VARCHAR(100) DEFAULT '';
	declare sallowprefix char(1);
	declare stextprefix varchar(5);
	declare sallowyop char(1);
	declare sallowmop char(1);
	declare sdoclength int(2);
	declare sdocnumfmt varchar(30);
	declare sdocnum int(2);
	declare stextprefix_last varchar(5);
	declare syop_last int(11);
	declare smop_last int(11);
	declare slebardok,slebardok1 int(11);
	declare scyop,scmop char(2) default '';
	
	set scyop=right(concat('0',syop),2),scmop=right(CONCAT('0',smop),2);
	select 	allowprefix,textprefix,allowyop,allowmop,doclength,docnumfmt,
		docnum,textprefix_last,yop_last,mop_last
	into 
		sallowprefix,stextprefix,sallowyop,sallowmop,sdoclength,sdocnumfmt,
		sdocnum,stextprefix_last,syop_last,smop_last 
	from 	setting_document where docid=sdocid ;
	
	set vc_docnum=sdocnumfmt;
	if sallowprefix='1' then
		SET vc_docnum=Replace(vc_docnum,'PP',stextprefix);
	else
		SET vc_docnum=REPLACE(vc_docnum,'PP','');
	end if;
	
	IF sallowyop='1' THEN
		SET vc_docnum=REPLACE(vc_docnum,'YY',scyop);
		
	ELSE
		SET vc_docnum=REPLACE(vc_docnum,'YY','');
		set syop=0;
		set syop_last=0;
	END IF;
	
	IF sallowmop='1' THEN
		SET vc_docnum=REPLACE(vc_docnum,'MM',scmop);
	ELSE
		SET vc_docnum=REPLACE(vc_docnum,'MM','');
		set smop=0;
		SET smop_last=0;
	END IF;
	
	
	if (syop*100+smop)!=(ifnull(syop_last,0)*100+ifnull(smop_last,0)) then
			set sdocnum=0;
	end if;
		
	
	SET vc_docnum_awl=vc_docnum;
	set slebardok1=(LENGTH(REPLACE(vc_docnum,'999','')));
	SET slebardok=sdoclength-slebardok1;
	
	SET vc_docnum=REPLACE(vc_docnum,'999',RIGHT(CONCAT(REPEAT('0',slebardok),sdocnum),slebardok));
	
	
	case sdocid
	WHEN 1 THEN
		set sdocnum=1;
	WHEN 2 THEN
		SET sdocnum=1;
	when 3 then
		if (select count(*) from transaksi_kwitansi  where nodokumen=vc_docnum)=1 then
		    SELECT IFNULL(MAX(RIGHT(nodokumen,slebardok)),0) into sdocnum 
		    FROM transaksi_kwitansi WHERE LEFT(nodokumen,slebardok1)=LEFT(vc_docnum,slebardok1);
		end if;
	
	WHEN 4 THEN
		SET sdocnum=1;
	WHEN 5 THEN
		SET sdocnum=1;
	WHEN 6 THEN
		IF (SELECT COUNT(*) FROM transaksi_masuk  WHERE nodokumen=vc_docnum)=1 THEN
		    SELECT IFNULL(MAX(RIGHT(nodokumen,slebardok)),0) INTO sdocnum 
		    FROM transaksi_masuk WHERE LEFT(nodokumen,slebardok1)=LEFT(vc_docnum,slebardok1);
		END IF;
	WHEN 7 THEN
		IF (SELECT COUNT(*) FROM transaksi_masuk_lain  WHERE nodokumen=vc_docnum)=1 THEN
		    SELECT IFNULL(MAX(RIGHT(nodokumen,slebardok)),0) INTO sdocnum 
		    FROM transaksi_masuk_lain WHERE LEFT(nodokumen,slebardok1)=LEFT(vc_docnum,slebardok1);
		END IF;
	WHEN 8 THEN
		IF (SELECT COUNT(*) FROM transaksi_keluar  WHERE nodokumen=vc_docnum)=1 THEN
		    SELECT IFNULL(MAX(RIGHT(nodokumen,slebardok)),0) INTO sdocnum 
		    FROM transaksi_keluar WHERE LEFT(nodokumen,slebardok1)=LEFT(vc_docnum,slebardok1);
		END IF;
	WHEN 9 THEN
		IF (SELECT COUNT(*) FROM transaksi_keluar_lain  WHERE nodokumen=vc_docnum)=1 THEN
		    SELECT IFNULL(MAX(RIGHT(nodokumen,slebardok)),0) INTO sdocnum 
		    FROM transaksi_keluar_lain WHERE LEFT(nodokumen,slebardok1)=LEFT(vc_docnum,slebardok1);
		END IF;
	when 10 then
		SET sdocnum=1;
	WHEN 11 THEN
		IF (SELECT COUNT(*) FROM transaksi_transfer  WHERE nodokumen=vc_docnum)=1 THEN
		    SELECT IFNULL(MAX(RIGHT(nodokumen,slebardok)),0) INTO sdocnum 
		    FROM transaksi_transfer WHERE LEFT(nodokumen,slebardok1)=LEFT(vc_docnum,slebardok1);
		END IF;
	when 12 then
		SET sdocnum=1;
	WHEN 13 THEN
		IF (SELECT COUNT(*) FROM trnent_gl  WHERE notran=vc_docnum)=1 THEN
		    SELECT IFNULL(MAX(RIGHT(notran,slebardok)),0) INTO sdocnum 
		    FROM trnent_gl WHERE LEFT(notran,slebardok1)=LEFT(vc_docnum,slebardok1);
		END IF;
	WHEN 14 THEN
		IF (SELECT COUNT(*) FROM transaksi_lhpp  WHERE nodokumen=vc_docnum)=1 THEN
		    SELECT IFNULL(MAX(RIGHT(nodokumen,slebardok)),0) INTO sdocnum 
		    FROM transaksi_lhpp WHERE LEFT(nodokumen,slebardok1)=LEFT(vc_docnum,slebardok1);
		END IF;
	WHEN 15 THEN
		IF (SELECT COUNT(*) FROM transaksi_lhpp_entry  WHERE nodokumen=vc_docnum)=1 THEN
		    SELECT IFNULL(MAX(RIGHT(nodokumen,slebardok)),0) INTO sdocnum 
		    FROM transaksi_lhpp_entry WHERE LEFT(nodokumen,slebardok1)=LEFT(vc_docnum,slebardok1);
		END IF;
	WHEN 16 THEN
		IF (SELECT COUNT(*) FROM transaksi_lhpp_tf  WHERE nodokumen=vc_docnum)=1 THEN
		    SELECT IFNULL(MAX(RIGHT(nodokumen,slebardok)),0) INTO sdocnum 
		    FROM transaksi_lhpp_tf WHERE LEFT(nodokumen,slebardok1)=LEFT(vc_docnum,slebardok1);
		END IF;	
	
	end case;
	
	SET sdocnum=sdocnum+1;
	
	UPDATE 	setting_document SET textprefix_last=stextprefix,yop_last=syop,mop_last=smop,docnum=sdocnum
	WHERE docid=sdocid;
	
	SET vc_docnum=REPLACE(vc_docnum_awl,'999',RIGHT(CONCAT(REPEAT('0',slebardok),sdocnum),slebardok));	
