/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 FUNCTION `fxget_conversi_sum_formula`(stextformulation VARCHAR(100)) RETURNS varchar(100) CHARSET latin1
BEGIN
	
	DECLARE stextformulation_new VARCHAR(100) ;
	DECLARE stextformulation_old,stextrow VARCHAR(100) ;
	DECLARE skey,scounter INT DEFAULT 100;
	declare spsssum,spssfr,spssto,snofr ,snoto,srowno,skeyrowno int;
	SET stextformulation_old=stextformulation;
	SET skey=CHAR_LENGTH(stextformulation_old);
	WHILE NOT skey=0 DO
		set spsssum=POSITION('sum(' IN stextformulation_old);
		if (spsssum)<>0 then
			set spssfr=POSITION('(' IN stextformulation_old)+1;
			SET spssto=POSITION(';' IN stextformulation_old);
			set snofr=mid(stextformulation_old,spssfr,spssto-spssfr);
			SET spssfr=POSITION(';' IN stextformulation_old)+1;
			SET spssto=POSITION(')' IN stextformulation_old);
			SET snoto=MID(stextformulation_old,spssfr,spssto-spssfr);
			set stextformulation=concat_ws(',',snofr ,snoto,spsssum,MID(stextformulation_old,spsssum,1+spssto-spsssum));
			set srowno=snofr;
			set skeyrowno=snoto;
			while srowno<=skeyrowno do
				SET stextrow=CONCAT_WS('+',stextrow,srowno) ;
				set srowno=srowno+1;
			end while;
			set stextformulation_old=replace(stextformulation_old,MID(stextformulation_old,spsssum,1+spssto-spsssum),stextrow);
			SET skey=POSITION('sum(' IN stextformulation_old);
		end if;
		IF scounter=0 THEN 
			SET skey=0;
		END IF;	 
		set scounter=scounter-1;
	END WHILE;
	SET stextformulation_new=stextformulation_old;
	
	RETURN stextformulation_new;
END */;;
DELIMITER ;
