/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 FUNCTION `fxget_textformulation`(sdocentry int,stextformulation varchar(100),sbalance int,saudituser VARCHAR(10),scmpnyid varchar(20)) RETURNS double
