/* SELECT fxget_textformulation(2,'2',1,'Amir','Accapp') */
	declare skeychar char(1);
	DECLARE stotal , sdetailamt,sdetailamtsebelumnya DOUBLE default 0;
	declare shitung char(1); /* 1 Plus 2 Minus 3 bagi */
	declare stextformulationasal varchar(100) default stextformulation;
	DECLARE i,ipos,nkey,nkey2,sno INT DEFAULT 1;
	declare nkeyhitung int DEFAULT 100; 
	DECLARE slenght INT ; 
