	WHILE NOT nkey=0 DO
		SET slenght=CHAR_LENGTH(stextformulation);
		
		SET nkey2=POSITION('+' IN stextformulation);
		if nkey2<nkeyhitung and nkey2<>0 then
		    	set nkeyhitung=nkey2;
		    	set shitung=1;
		    	set skeychar='+';
