		SET nkey2=POSITION('-' IN stextformulation);
		IF nkey2<nkeyhitung AND nkey2<>0 THEN
		    	SET nkeyhitung=100;
		    	SET shitung=2;
		    	SET skeychar='-';
		END IF;
		SET nkeyhitung=100;
		SET nkey=POSITION(skeychar IN stextformulation);
		
		IF nkey<>0 THEN
			SET schar = SUBSTR(stextformulation,1,nkey-1);
			set schargabungan=schargabungan+schar;
			
			
			set sdetailamt=ifnull((SELECT sum(IF(sbalance=1,openbalance,IF(sbalance=2,currentbalance,endbalance))) AS openbalance
			FROM op_financial_report WHERE docentry=sdocentry AND rowno=schar AND audituser=saudituser
			group by rowno ),0);
					
			set stextformulationasal=replace(stextformulationasal,schar,
			(SELECT sum(if(sbalance=1,openbalance,if(sbalance=2,currentbalance,endbalance))) as openbalance
			FROM op_financial_report WHERE docentry=sdocentry AND rowno=schar
			GROUP BY rowno));
			
			IF shitungsebelumnya=1 THEN
				set sdetailamt=sdetailamt*1;
			END IF;
			IF shitungsebelumnya=2 THEN
				set sdetailamt=sdetailamt*-1;
			END IF;
			SET stotal=stotal+sdetailamt;
			SET shitungsebelumnya=shitung;
		
			
			set sdetailamtsebelumnya=sdetailamt;
			SET sno=sno+1;
			SET ipos=nkey+1;
			SET stextformulation=RIGHT(stextformulation,slenght-ipos+1);
		END IF;
		SET i=i+1;
		END WHILE;
		set schargabungan=schargabungan+stextformulation;
		
		SET sdetailamt=ifnull((SELECT sum(IF(sbalance=1,openbalance,IF(sbalance=2,currentbalance,endbalance))) AS openbalance
