/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 FUNCTION `fxget_textformulation_lama`(sdocentry int,stextformulation varchar(100),sbalance int,saudituser VARCHAR(10),scmpnyid varchar(20)) RETURNS double
BEGIN
/* select fxget_textformulation(1,'21,4+7+10+13',1,'Amir','Accapp') */
	declare skeychar char(1);
	DECLARE stotal , sdetailamt,sdetailamtsebelumnya DOUBLE default 0;
	declare shitung char(1); /* 1 Plus 2 Minus 3 bagi */
	declare stextformulationasal varchar(100) default stextformulation;
	DECLARE i,ipos,nkey,nkey2,sno INT DEFAULT 1;
	declare nkeyhitung int DEFAULT 100; 
	DECLARE slenght INT ; 
	DECLARE schar VARCHAR(100) ;
	declare schargabungan varchar(100) default '';
	declare shitungsebelumnya char(1) default 1;
	set stextformulation=concat('+',stextformulation);
	WHILE NOT nkey=0 DO
		SET slenght=CHAR_LENGTH(stextformulation);
		
		SET nkey2=POSITION('+' IN stextformulation);
		if nkey2<nkeyhitung and nkey2<>0 then
		    	set nkeyhitung=nkey2;
		    	set shitung=1;
		    	set skeychar='+';
		end if;
		
		SET nkey2=POSITION('-' IN stextformulation);
		IF nkey2<nkeyhitung AND nkey2<>0 THEN
		    	SET nkeyhitung=100;
		    	SET shitung=2;
		    	SET skeychar='-';
		END IF;
		SET nkeyhitung=100;
		SET nkey=POSITION(skeychar IN stextformulation);
		
		IF nkey<>0 THEN
			SET schar = SUBSTR(stextformulation,1,nkey-1);
			set schargabungan=schargabungan+schar;
			
			
			set sdetailamt=ifnull((SELECT IF(sbalance=1,openbalance,IF(sbalance=2,currentbalance,endbalance)) AS openbalance
			FROM op_financial_report WHERE docentry=sdocentry AND rowno=schar AND audituser=saudituser),0);
					
			set stextformulationasal=replace(stextformulationasal,schar,
			(SELECT if(sbalance=1,openbalance,if(sbalance=2,currentbalance,endbalance)) as openbalance
			FROM op_financial_report WHERE docentry=sdocentry AND rowno=schar));
			
			IF shitungsebelumnya=1 THEN
				set sdetailamt=sdetailamt*1;
			END IF;
			IF shitungsebelumnya=2 THEN
				set sdetailamt=sdetailamt*-1;
			END IF;
			SET stotal=stotal+sdetailamt;
			SET shitungsebelumnya=shitung;
		
			
			set sdetailamtsebelumnya=sdetailamt;
			SET sno=sno+1;
			SET ipos=nkey+1;
			SET stextformulation=RIGHT(stextformulation,slenght-ipos+1);
		END IF;
		SET i=i+1;
		END WHILE;
		set schargabungan=schargabungan+stextformulation;
		
		SET sdetailamt=ifnull((SELECT IF(sbalance=1,openbalance,IF(sbalance=2,currentbalance,endbalance)) AS openbalance
			FROM op_financial_report WHERE docentry=sdocentry AND rowno=stextformulation AND audituser=saudituser),0) ;
			
		
		SET stextformulationasal=REPLACE(stextformulationasal,stextformulation,
			(SELECT IF(sbalance=1,openbalance,IF(sbalance=2,currentbalance,endbalance)) AS openbalance
			FROM op_financial_report WHERE docentry=sdocentry AND rowno=stextformulation));
		
			IF shitung=1 THEN
				SET sdetailamt=sdetailamt*1;
			END IF;
			IF shitung=2 THEN
				SET sdetailamt=sdetailamt*-1;
			END IF;
			SET stotal=stotal+sdetailamt;
	
	RETURN	stotal	;
	/*RETURN stextformulationasal;*/
    END */;;
