/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spmutasi_stock_rpt`()
BEGIN
SELECT 	a.kodegudang,mg.namagudang,a.kodeproduk,mp.namaproduk,
	mp.kodebrand,mp.kodekategori,mp.kodefungsi,
	a.msaldoawal,a.mpembelian,a.mtranfin,a.mmasuklain,
	a.kjual,a.ktransout,a.kkeluarlain,
	(a.msaldoawal+a.mpembelian+a.mtranfin+a.mmasuklain) AS masuk,
	(a.kjual+a.ktransout+a.kkeluarlain) AS keluar,
	(a.msaldoawal+a.mpembelian+a.mtranfin+a.mmasuklain)-(a.kjual+a.ktransout+a.kkeluarlain) AS saldoakhir,
	IF('sortby'='1','By Brand',IF('sortby'='2','By Kategori','By Fungsi')) AS sortbytext,
	IF('sortby'='1',mp.kodebrand,IF('sortby'='2',mp.kodekategori,mp.kodefungsi)) AS sortby,
	IF('sortby'='1',mb.namabrand,IF('sortby'='2',mk.namakategori,mf.namafungsi)) AS sortbydesc
FROM
(
SELECT 	 kodegudang,kodeproduk,
	SUM(msaldoawal) AS msaldoawal,SUM(mpembelian) AS mpembelian,SUM(mtranfin) AS mtranfin,SUM(mmasuklain) AS mmasuklain,
	SUM(kjual) AS kjual,SUM(ktransout) AS ktransout,SUM(kkeluarlain) AS kkeluarlain
FROM
(
SELECT kodegudang,kodeproduk,
	stock AS msaldoawal,0 AS mpembelian,0 AS mtranfin,0 AS mmasuklain,
	0 AS kjual,0 AS ktransout,0 AS kkeluarlain FROM master_stok_balance
WHERE yop=IF(10=1,2011-1,2011) AND mop =IF(10=1,12,10) AND kodegudang='001'
UNION ALL
SELECT b.kodegudang,b.kodeproduk,
	0 AS msaldoawal,b.jumlah AS mpembelian,0 AS mtranfin,0 AS mmasuklain,
	0 AS kjual,0 AS ktransout,0 AS kkeluarlain
FROM transaksi_masuk a
INNER JOIN transaksi_masuk_detail1 b ON a.docentry=b.docentry AND 
YEAR(a.tgldokumen)=2011 AND MONTH(a.tgldokumen)=10 AND b.kodegudang='001'
UNION ALL
SELECT b.kodegudangto,b.kodeproduk,
	0 AS msaldoawal,0 AS mpembelian,b.jumlah AS mtranfin,0 AS mmasuklain,
	0 AS kjual,0 AS ktransout,0 AS kkeluarlain
FROM transaksi_transfer a
INNER JOIN transaksi_transfer_detail1 b ON a.docentry=b.docentry AND 
YEAR(a.tgldokumen)=2011 AND MONTH(a.tgldokumen)=10 AND b.kodegudangto='001'
UNION ALL
SELECT b.kodegudang,b.kodeproduk,
	0 AS msaldoawal,0 AS mpembelian,0 AS mtranfin,b.jumlah AS mmasuklain,
	0 AS kjual,0 AS ktransout,0 AS kkeluarlain
FROM transaksi_masuk_lain a
INNER JOIN transaksi_masuk_lain_detail1 b ON a.docentry=b.docentry AND 
YEAR(a.tgldokumen)=2011 AND MONTH(a.tgldokumen)=10 AND b.kodegudang='001'
UNION ALL
SELECT b.kodegudang,b.kodeproduk,
	0 AS msaldoawal,0 AS mpembelian,0 AS mtranfin,0 AS mmasuklain,
	b.jumlah AS kjual,0 AS ktransout,0 AS kkeluarlain
FROM transaksi_keluar a
INNER JOIN transaksi_keluar_detail1 b ON a.docentry=b.docentry AND 
YEAR(a.tgldokumen)=2011 AND MONTH(a.tgldokumen)=10 AND b.kodegudang='001'
UNION ALL
SELECT b.kodegudangfr,b.kodeproduk,
	0 AS msaldoawal,0 AS mpembelian,0 AS mtranfin,0 AS mmasuklain,
	0 AS kjual,b.jumlah AS ktransout,0 AS kkeluarlain
FROM transaksi_transfer a
INNER JOIN transaksi_transfer_detail1 b ON a.docentry=b.docentry AND 
YEAR(a.tgldokumen)=2011 AND MONTH(a.tgldokumen)=10 AND b.kodegudangfr='001'
UNION ALL
SELECT b.kodegudang,b.kodeproduk,
	0 AS msaldoawal,0 AS mpembelian,0 AS mtranfin,0 AS mmasuklain,
	0 AS kjual,0 AS ktransout,b.jumlah AS kkeluarlain
FROM transaksi_keluar_lain a
INNER JOIN transaksi_keluar_lain_detail1 b ON a.docentry=b.docentry AND 
YEAR(a.tgldokumen)=2011 AND MONTH(a.tgldokumen)=10 AND b.kodegudang='001'
) AS a
GROUP BY  kodegudang,kodeproduk
) AS a
INNER JOIN master_produk mp ON mp.kodeproduk=a.kodeproduk
INNER JOIN master_gudang mg ON mg.kodegudang=a.kodegudang
LEFT JOIN master_brand mb ON mb.kodebrand=mp.kodebrand
LEFT JOIN master_kategori mk ON mk.kodekategori=mp.kodekategori
LEFT JOIN master_fungsi mf ON mf.kodefungsi=mp.kodefungsi;
    END */;;
DELIMITER ;
