/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spprocess_postinggl`(in saudituser varchar(10))
BEGIN
	/* insert master coa yg belum ada di tblglmasbal */
	INSERT INTO tblglmasbal
	( kd_wil,yop,coa,entrydate,auditdate,audituser )
	SELECT kd_wil,yop,coa,entrydate,auditdate,audituser
	FROM
	(	
	SELECT '01' AS kd_wil,a.yop,b.coa,NOW() AS entrydate,NOW() AS auditdate,a.audituser
	FROM (
	SELECT 	yop,audituser
	FROM transaksi_kwitansi_temp WHERE audituser=''
	GROUP BY yop,audituser ) AS a,
	(SELECT coa FROM tblglmas WHERE STATUS='Y' ) AS b
	) AS a WHERE NOT EXISTS (SELECT coa FROM tblglmasbal WHERE a.yop=yop AND a.kd_wil='01' AND a.coa=coa); 
	UPDATE tblglmasbal a , 
	(
	SELECT a.yop,a.coa,
	SUM(IF(a.mop=1,ttlamount,0)) AS nilper01, SUM(IF(a.mop=2,ttlamount,0)) AS nilper02, SUM(IF(a.mop=3,ttlamount,0)) AS nilper03, 
	SUM(IF(a.mop=4,ttlamount,0)) AS nilper04, SUM(IF(a.mop=5,ttlamount,0)) AS nilper05, SUM(IF(a.mop=6,ttlamount,0)) AS nilper06, 
	SUM(IF(a.mop=7,ttlamount,0)) AS nilper07, SUM(IF(a.mop=8,ttlamount,0)) AS nilper08, SUM(IF(a.mop=9,ttlamount,0)) AS nilper09, 
	SUM(IF(a.mop=10,ttlamount,0)) AS nilper10, SUM(IF(a.mop=11,ttlamount,0)) AS nilper11, SUM(IF(a.mop=12,ttlamount,0)) AS nilper12
	FROM
	(
	SELECT a.yop,a.mop,a1.coa,SUM(a1.jumdebet-a1.jumkredit) AS ttlamount FROM trnent_gl a 
	INNER JOIN trnent_gldetail1 a1 ON a.docentry=a1.docentry 
	inner join transaksi_kwitansi_temp b on a.yop=b.yop and a.mop=b.mop and b.audituser=saudituser
	and a.proses='N' and a.glstatus='1'
	GROUP BY a.yop,a.mop,a1.coa
	) AS a
	GROUP BY a.yop,a.coa
	) AS b
	SET 
	a.nilper01=a.nilper01+b.nilper01,
	a.nilper02=a.nilper02+b.nilper02,
	a.nilper03=a.nilper03+b.nilper03,
	a.nilper04=a.nilper04+b.nilper04,
	a.nilper05=a.nilper05+b.nilper05,
	a.nilper06=a.nilper06+b.nilper06,
	a.nilper07=a.nilper07+b.nilper07,
	a.nilper08=a.nilper08+b.nilper08,
	a.nilper09=a.nilper09+b.nilper09,
	a.nilper10=a.nilper10+b.nilper10,
	a.nilper11=a.nilper11+b.nilper11,
	a.nilper12=a.nilper12+b.nilper12
	WHERE a.kd_wil='01' AND a.coa=b.coa AND a.yop=b.yop;
	
	update trnent_gl a,
	(SELECT audituser, yop, mop  FROM transaksi_kwitansi_temp ) as b
	set a.proses='Y',a.prosesdate=now(),a.glstatus='2'
	where a.proses='N' and a.glstatus='1' and a.yop=b.yop AND a.mop=b.mop AND b.audituser=saudituser;
	
    END */;;
DELIMITER ;
