/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_aging_report_detail`(in 
    skodecustomer varchar(15), scutoff varchar(10),
    sallcustomer char(1),sget int)
BEGIN
if sget=0 then
	SELECT a1.kodecustomer,a1.namacustomer,concat(a1.kodecustomer,'-',a1.namacustomer) as custmrQ,
	scutoff AS cutoffaging,@days:=DATEDIFF(scutoff,tgljtfaktur) AS c,
	concat(a.nodokumen,' ',IF(k1.nodokumen IS NULL,'*','')) as nodokumen,tgldokumen,tgljtfaktur,tukarfaktur,tgltukarfaktur,
	totalsetppn nilfaktur,totalbayar,totalsisafaktur,
	IF(@days<1,totalsisafaktur,0) AS agCrnt,
	IF(@days BETWEEN 1 AND 14 ,totalsisafaktur,0) AS ag1to14,
	IF(@days BETWEEN 15 AND 30,totalsisafaktur,0) AS ag15to30,
	IF(@days BETWEEN 31 AND 60,totalsisafaktur,0) AS ag31to60,
	IF(@days BETWEEN 61 AND 90,totalsisafaktur,0) AS ag61to90,
	IF(@days >90,totalsisafaktur,0) AS agdiatas90,p.CmnyName,p.Address1,p.City,p.Faximale,p.Phone1,
	if(k1.nodokumen is null,'*','') as tanda
	FROM transaksi_keluar_tfaktur a INNER JOIN master_customer a1 ON a1.kodecustomer=a.kodecustomer
	left join (select nodokumen from transaksi_kwitansidetail1 group by nodokumen) k1 on k1.nodokumen=a.nodokumen
	,master_preferences p
	WHERE 	totalsisafaktur!=0 AND if(sallcustomer='Y',true,a.kodecustomer=skodecustomer) and a.tukarfaktur='Y'
	order by a1.kodecustomer,nodokumen
	;
end if;
IF sget=1 THEN
	
	SELECT count(*)
	FROM transaksi_keluar_tfaktur a INNER JOIN master_customer a1 ON a1.kodecustomer=a.kodecustomer
	,master_preferences p
	WHERE 	totalsisafaktur!=0 AND IF(sallcustomer='Y',TRUE,a.kodecustomer=skodecustomer) AND a.tukarfaktur='Y'
	;
END IF;
    END */;;
