/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 PROCEDURE `sp_apply_kwitansi_from_lhpp`(in snokwitansi varchar(15),stot_bayar_kwitansi decimal(12,2),sBayarSts char(1))
BEGIN
	  DECLARE done INT DEFAULT 0;
	  DECLARE stextheader,stextformulation CHAR(100);
	  declare snodokumen VARCHAR(15);
	  declare stotalbayar , stotalsisafaktur,sjumlahbayar decimal(12,2) default 0;
	  DECLARE cur1 CURSOR FOR 
	  
	  
		SELECT a1.nodokumen,a3.totalbayar, a3.totalsisafaktur FROM transaksi_kwitansi a 
		INNER JOIN transaksi_kwitansidetail1 a1 ON a.docentry=a1.docentry
		INNER JOIN transaksi_keluar_tfaktur a3 ON a3.nodokumen=a1.nodokumen
	        WHERE a.nodokumen=snokwitansi ORDER BY a1.docentry,a1.linenum;
	  
 
	  DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;
	  OPEN cur1;
	  REPEAT
	    FETCH cur1 INTO snodokumen,stotalbayar , stotalsisafaktur;
	    IF NOT done THEN
		
		if sBayarSts='Y' then 	
			set sjumlahbayar=if(stotalsisafaktur<=stot_bayar_kwitansi,stotalsisafaktur,stot_bayar_kwitansi);
			update transaksi_keluar_tfaktur set totalbayar=totalbayar+sjumlahbayar,totalsisafaktur=totalsisafaktur-sjumlahbayar
			where nodokumen=snodokumen;
			set stot_bayar_kwitansi=stot_bayar_kwitansi-sjumlahbayar;
		end if;
		
		IF sBayarSts='N' THEN 	
			SET sjumlahbayar=IF(stotalbayar<=stot_bayar_kwitansi,stotalbayar,stot_bayar_kwitansi);
			UPDATE transaksi_keluar_tfaktur SET totalbayar=totalbayar-sjumlahbayar,totalsisafaktur=totalsisafaktur+sjumlahbayar
			WHERE nodokumen=snodokumen;
			SET stot_bayar_kwitansi=stot_bayar_kwitansi-sjumlahbayar;
		END IF;
		
	       
	    END IF;
	  UNTIL done END REPEAT;
	  CLOSE cur1;
	  
    END */;;
DELIMITER ;
