/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 PROCEDURE `sp_create_financial_report`(IN syop integer,smop integer,sdocentry INT(11),saudituser varchar(10),scmpnyid varchar(20))
BEGIN
declare sreprocess char(1);
