/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 PROCEDURE `sp_create_financial_report_proses`(IN syop integer,smop integer,sdocentry INT(11),saudituser varchar(10),scmpnyid varchar(20))
BEGIN
  /* call sp_create_financial_report(2011,11,1,'Amir','Accapp') */  
  DECLARE done INT DEFAULT 0;
  DECLARE Clicks INT;
  DECLARE skeyrecal INT DEFAULT 0;
	declare slineno int(11);
	DECLARE srowno INT(11);
	DECLARE scolumntype CHAR(1);
	DECLARE slevelno INT(1);
	DECLARE stextheader VARCHAR(100);
	DECLARE sallowformulation CHAR(1);
	DECLARE ssummary CHAR(1);
	DECLARE stextformulation VARCHAR(100);
	DECLARE svisorder INT(11);
	DECLARE sshowdata char(1);
	
	DECLARE i,ipos,nkey,sno INT DEFAULT 1;
	DECLARE slenght INT ; 
	DECLARE schar VARCHAR(100) ;
 
  DECLARE cur1 CURSOR FOR 
  SELECT rowno,columntype,levelno,textheader, 
	 allowformulation,summary,textformulation,visorder,showdata
  FROM 	 setting_financial_rpt_detail1 WHERE docentry=sdocentry
  order by visorder asc;
  DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;
  
  DELETE FROM op_financial_report WHERE docentry=sdocentry AND audituser=saudituser and cmpnyid=scmpnyid;
  set slineno=1;
  OPEN cur1;
  REPEAT
  FETCH cur1 INTO 
  srowno,scolumntype,slevelno,stextheader,sallowformulation,
  ssummary,stextformulation,svisorder,sshowdata;
    IF NOT done THEN
	if scolumntype in (1) then
		insert into op_financial_report 
		(docentry,lineno,rowno,columntype,levelno,txtnoaccount,txtdescription,auditdate,audituser,cmpnyid,visorder,showdata)
		SELECT sdocentry,slineno,srowno,scolumntype,slevelno,concat("'",stextformulation,"'") as txtnoaccount,ifnull(stextheader,'') as txtdescription,now(),saudituser,scmpnyid,svisorder,sshowdata;
		set slineno=slineno+1;
	end if ;
	
	IF scolumntype=2 THEN
		SET nkey=POSITION(',' IN stextformulation);
		WHILE NOT nkey=0 DO
		SET slenght=CHAR_LENGTH(stextformulation);
		SET nkey=POSITION(',' IN stextformulation);
		IF nkey<>0 THEN
			SET schar = SUBSTR(stextformulation,1,nkey-1);
			INSERT INTO op_financial_report 
			(docentry,lineno,rowno,columntype,levelno,txtnoaccount,txtdescription,openbalance,currentbalance,endbalance,auditdate,audituser,cmpnyid,visorder,showdata)
			SELECT sdocentry,@lineno:=@lineno+1 AS lineno,srowno,scolumntype,slevelno,a.accfmttd AS txtnoaccount,b.accdesc AS txtdescription,openbalance,currentbalance,endbalance,now(),saudituser,scmpnyid,svisorder,sshowdata
			FROM 
			(SELECT acccode,accfmttd,SUM(openbalance) AS openbalance,SUM(currentbalance)-SUM(openbalance) AS currentbalance,SUM(endbalance) AS endbalance
			FROM 
			(
			SELECT acccode,accfmttd,dbtopenbal,crtopenbal,(dbtopenbal-crtopenbal) AS openbalance,0 AS currentbalance,0  AS endbalance
			FROM gl_accountbalance 
			WHERE yop=IF(smop=1,syop-1,syop) AND mop=IF(smop=1,12,smop-1) AND acccode LIKE (schar) AND curnccode='IDR'
			UNION ALL
			SELECT acccode,accfmttd,dbtopenbal,crtopenbal,0 AS openbalance,(dbtopenbal-crtopenbal) AS currentbalance,(dbtopenbal-crtopenbal)  AS endbalance
			FROM gl_accountbalance 
			WHERE yop=syop AND mop=smop AND acccode LIKE (schar) AND curnccode='IDR'
			) AS a
			GROUP BY acccode,accfmttd
			) AS a INNER JOIN gl_account b ON a.acccode=b.acccode ,(SELECT @lineno:=slineno-1) AS lineno;
			SET slineno=@lineno+1;
			
			SET sno=sno+1;
			SET ipos=nkey+1;
			SET stextformulation=RIGHT(stextformulation,slenght-ipos+1);
		END IF;
		SET i=i+1;
		END WHILE;
			SET schar = stextformulation;
			INSERT INTO op_financial_report 
			(docentry,lineno,rowno,columntype,levelno,txtnoaccount,txtdescription,openbalance,currentbalance,endbalance,auditdate,audituser,cmpnyid,visorder,showdata)
			SELECT sdocentry,@lineno:=@lineno+1 as slineno,srowno,scolumntype,slevelno,a.accfmttd AS txtnoaccount,b.accdesc AS txtdescription,openbalance,currentbalance,endbalance,NOW(),saudituser,scmpnyid,svisorder,sshowdata
			From
			(SELECT acccode,accfmttd,SUM(openbalance) AS openbalance,SUM(currentbalance)-SUM(openbalance) AS currentbalance,SUM(endbalance) AS endbalance
			FROM 
			(
			SELECT acccode,accfmttd,dbtopenbal,crtopenbal,(dbtopenbal-crtopenbal) AS openbalance,0 AS currentbalance,0  AS endbalance
					FROM gl_accountbalance WHERE yop=IF(smop=1,syop-1,syop) AND mop=IF(smop=1,12,smop-1) AND acccode LIKE (schar) AND curnccode='IDR'
					UNION ALL
			SELECT acccode,accfmttd,dbtopenbal,crtopenbal,0 AS openbalance,(dbtopenbal-crtopenbal) AS currentbalance,(dbtopenbal-crtopenbal)  AS endbalance
					FROM gl_accountbalance WHERE yop=syop AND mop=smop AND acccode LIKE (schar) AND curnccode='IDR'
			) AS a
			GROUP BY acccode,accfmttd
			) AS a INNER JOIN gl_account b ON a.acccode=b.acccode ,(select @lineno:=slineno-1) as lineno;
			SET slineno=@lineno+1;
			
	end if;
	
	
	IF scolumntype IN (3) THEN
		INSERT INTO op_financial_report 
		(docentry,lineno,rowno,columntype,levelno,txtnoaccount,txtdescription,auditdate,audituser,cmpnyid,visorder,showdata)
		SELECT sdocentry,slineno,srowno,scolumntype,slevelno,'' AS txtnoaccount,IFNULL(stextheader,'') AS txtdescription,now(),saudituser,scmpnyid,svisorder,sshowdata;
		SET slineno=slineno+1;
	END IF ;
    END IF;     
  UNTIL done END REPEAT;
  close cur1;
  
  set i=1,ipos=1,nkey=1,sno =1;
  SET done = 0;
  OPEN cur1;
  REPEAT
  FETCH cur1 INTO 
  srowno,scolumntype,slevelno,stextheader,sallowformulation,
  ssummary,stextformulation,svisorder,sshowdata;
    IF NOT done THEN
	
	IF scolumntype IN (3) and sallowformulation='Y' THEN
		if ssummary='Y' then
		
		update op_financial_report a,
		(select sum(openbalance) as openbalance,SUM(currentbalance) AS currentbalance,SUM(endbalance) AS endbalance
		from op_financial_report where docentry=sdocentry and rowno=stextformulation) as b
		set a.openbalance=b.openbalance,a.currentbalance=b.currentbalance,a.endbalance=b.endbalance
		where a.docentry=sdocentry and rowno=srowno and docentry=sdocentry AND audituser=saudituser;
		
		end if;
		IF ssummary='N' THEN
		
		UPDATE op_financial_report a,
		(SELECT	fxget_textformulation(sdocentry,stextformulation,1,saudituser,scmpnyid) as openbalance,
			fxget_textformulation(sdocentry,stextformulation,2,saudituser,scmpnyid) AS currentbalance,
			fxget_textformulation(sdocentry,stextformulation,3,saudituser,scmpnyid) AS endbalance
		) as b
		SET a.openbalance=b.openbalance,a.currentbalance=b.currentbalance,a.endbalance=b.endbalance
		WHERE a.docentry=sdocentry AND rowno=srowno AND audituser=saudituser;
		
		END IF;
	END IF ;
    END IF;     
  UNTIL done END REPEAT;
  CLOSE cur1;
  update op_financial_report set txtnoaccount='' where columntype in (1,3) and docentry=sdocentry;
END */;;
DELIMITER ;
