					FROM gl_accountbalance WHERE yop=syop AND mop=smop AND acccode LIKE (stextformulation) AND curnccode='IDR'
			) AS a
			GROUP BY acccode,accfmttd
			) AS a INNER JOIN gl_account b ON a.acccode=b.acccode ;
		
		
	  
  END */;;
