/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 PROCEDURE `sp_get_gl_import_journal_entry_sts`(IN ssessiondate VARCHAR(10),scmpnyid varchar(20),saudituser VARCHAR(10))
BEGIN
	/* call sp_get_gl_import_journal_entry_sts('2012-02-22','Accapp','Admin') */
	DECLARE done INT DEFAULT 0;
	DECLARE sstatus,stextbtchentry,stextacccode,stextbalance,sfilename  VARCHAR(200) default 'Ok';
	DECLARE sbtchentry,sbtchentrykey DECIMAL(10,0) default 0;
	DECLARE sjournalid,sjournalidkey DECIMAL(10,0) default 0;
	DECLARE sjournaldesc VARCHAR(200);
	DECLARE stransnbr DECIMAL(10,0);
	DECLARE sacccode VARCHAR(25);
	DECLARE stransamt DECIMAL(15,0);
	
	DECLARE cur1 CURSOR FOR  
	SELECT btchentry,journalid,acccode 	 
	FROM gl_jed_temp  WHERE audituser=saudituser AND cmpnyid=scmpnyid
	GROUP BY btchentry,journalid,acccode;
	
	DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;
	OPEN cur1;
	REPEAT
	    FETCH cur1 INTO sbtchentry,sjournalid,sacccode;
	    IF NOT done THEN
		if sbtchentrykey<>sbtchentry then
			 
			if (select count(batchid) from gl_batchlist where batchid=sbtchentry)=0 then
				SET stextbtchentry=CONCAT(replace(stextbtchentry,'Ok',''),CONCAT(sbtchentry,','));
			end if;
			set sbtchentrykey=sbtchentry;
		end if;
		
		IF sjournalidkey<>sjournalid THEN
			set stransamt=(SELECT SUM(transamt) AS transamt	 
				FROM gl_jed_temp  WHERE audituser=saudituser AND cmpnyid=scmpnyid AND btchentry=sbtchentry
				AND journalid=sjournalid);
			IF not	stransamt=0 THEN
				SET stextbalance=CONCAT(replace(stextbalance,'Ok',''),concat(sbtchentry,'-',sjournalid,'(',stransamt,'),'));
			END IF;
			SET sjournalidkey=sjournalid;
		END IF;
		
		IF (select count(acccode) from gl_account where acccode=sacccode)=0 THEN
			SET stextacccode=CONCAT(replace(stextacccode,'Ok',''),CONCAT(sbtchentry,'-',sjournalid,'(',sacccode,'),'));
		END IF;
		
	    END IF;
	  UNTIL done END REPEAT;
	  CLOSE cur1;	 
	  if stextbtchentry='Ok' and stextbalance='Ok' and stextacccode='Ok' then
		set sstatus='Ok';
	  else 
		set stextbtchentry=if(stextbtchentry='Ok','',concat('Batch.No ',left(stextbtchentry,length(trim(stextbtchentry))-1),' Not Found'));
		SET stextbalance=IF(stextbalance='Ok','',CONCAT('Batch Entry ',LEFT(stextbalance,LENGTH(TRIM(stextbalance))-1),' Not Balance'));
		SET stextacccode=IF(stextacccode='Ok','',CONCAT('Account.no ',LEFT(stextacccode,LENGTH(TRIM(stextacccode))-1),' Not Found'));
		set sstatus=concat(stextbtchentry,if(stextbtchentry='','',','),stextbalance,if(stextacccode='','',','),stextacccode);
	  end if;
	  delete from gl_import_data_msg where audituser=saudituser AND cmpnyid=scmpnyid;
	  insert into gl_import_data_msg (importsts,txtmessage,auditdate,audituser,cmpnyid)
	  SELECT if(sstatus='Ok',1,0) as importsts, sstatus as txtmessage, now() as auditdate, saudituser as audituser,scmpnyid as cmpnyid;
END */;;
DELIMITER ;
