/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_get_monitoring_bayar`(IN syop int,smop int,snmlengkap varchar(50),sstsbayar char(1))
BEGIN
	/* sp_get_monitoring_bayar(2012,2,'',1) */
	set snmlengkap=concat('%',trim(snmlengkap),'%');
	
	SELECT  @NO:=@NO+1 AS Nourt,a.yop,a.mop,a.noidsiswa,a.nmlengkap,a.alamat,a.stsbayar,a.jnsbayar,
		SUM(biaya) AS biaya
	FROM
	(
	SELECT 	mkb.yop,mkb.mop,
	mk.noidsiswa,ms.nmlengkap,CONCAT_WS(',',ms.almtrumah1,ms.almtrumah2) AS alamat,
	mkb.stsbayar,mkb.jnsbayar,IF(mkb.stsbayar='0',mkb.nilaibayar,
	IF(mk.tingkatansklh='0',biayatk,
	IF(mk.tingkatansklh='1',biayasd,
	IF(mk.tingkatansklh='2',biayasmp,
	IF(mk.tingkatansklh='3',biayasma,biayadiatassma))))) AS biaya
	FROM master_kartu_bayar mkb
	INNER JOIN master_kelas  mk  ON mk.docentry=mkb.docentry and mkb.jnsbayar=sstsbayar AND MKB.yop=syop AND MKB.mop=smop and stskelas='1'
	INNER JOIN master_siswa ms ON ms.noidsiswa=mk.noidsiswa and nmlengkap like snmlengkap,
	(SELECT * FROM master_default_biaya WHERE biayaid=2) AS mdb
	order by mkb.yop,mkb.mop,ms.nmlengkap
	) AS a
	,
	(SELECT @NO:=0) AS Nourt
	GROUP BY a.yop,a.mop,a.nmlengkap,a.noidsiswa,a.alamat,a.stsbayar,a.jnsbayar
	;
