/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_get_monitoring_kelas_info`(in snoidsiswa varchar(15))
BEGIN
	SELECT 	@nourt:=@nourt+1 as nourt,mk.noidsiswa,mk.nokursus,mk.tglmulai,IF(mk.stskelas='1','Aktif',IF(mk.stskelas='2','Cuti','Tidak Aktif')) AS statuskls,
		mdp.keterangan AS kelas,
		IF(mk.tingkatansklh='0','TK',IF(mk.tingkatansklh='1','SD',
		IF(mk.tingkatansklh='2','SMP',	IF(mk.tingkatansklh='3',
		'SMA','Diatas SMA')))) AS tingkatansek
	FROM master_kelas mk
	LEFT JOIN master_default_pelajaran mdp ON mdp.kodegroup=mk.kodegroup AND mdp.pelajaran=mk.pelajaran,
	(select @nourt:=0) as nourt
