/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_get_siswa_ulang_tahun`(
in stanggalfr int,sbulanfr int,stanggalto INT,sbulanto INT,scek1 char(1),scek2 char(1),scek3 char(1))
BEGIN
SELECT `ns`.`noidsiswa`   AS `noidsiswa`,
  `ns`.`nmlengkap`   AS `nmlengkap`,
  `ns`.`jnskelamin`  AS `jnskelamin`,
  `ns`.`tptlahir`    AS `tptlahir`,
  `ns`.`tgllahir`    AS `tgllahir`,
  `ns`.`almtrumah1`  AS `almtrumah1`,
  `ns`.`notelprumah` AS `notelprumah`,
  IF((`ns`.`stssiswa` = _latin1'1'),_utf8'Aktif',IF((`ns`.`stssiswa` = _latin1'2'),_utf8'Cuti',_utf8'Tidak Aktif')) AS `stssiswa`,
  GROUP_CONCAT(TRIM(CONCAT(`mk`.`nokursus`,_latin1'-',`mdp`.`keterangan`)) SEPARATOR ',') AS `kelas`
FROM ((`master_siswa` `ns`
    JOIN `master_kelas` `mk`
      ON ((`mk`.`noidsiswa` = `ns`.`noidsiswa`)))
   JOIN `master_default_pelajaran` `mdp`
     ON (((`mdp`.`pelajaran` = `mk`.`pelajaran`)
          AND (`mdp`.`kodegroup` = `mk`.`kodegroup`))))
where month(`ns`.`tgllahir`)*100+day(`ns`.`tgllahir`) between sbulanfr*100+stanggalfr and sbulanto*100+stanggalto 
and (`ns`.`stssiswa`=if(scek1='1','1','9') or `ns`.`stssiswa`=IF(scek2='1','2','9') or `ns`.`stssiswa`=IF(scek3='1','0','9'))
GROUP BY `ns`.`noidsiswa`
order by MONTH(`ns`.`tgllahir`)*100+DAY(`ns`.`tgllahir`),`ns`.`nmlengkap` ;
    END */;;
DELIMITER ;
