/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 PROCEDURE `sp_insert_transaksi_kwitansidetail2_khusus`(in sdocentry int(11),sbatas int(11))
BEGIN
	  DECLARE done INT DEFAULT 0;
	  DECLARE stgldokumen varchar(11);
	  declare stxtnodokumen CHAR(150);
	  declare slinenum int(11);
	  declare sbaris int(11);
	 
	  DECLARE cur1 CURSOR FOR SELECT DATE_FORMAT(tgldokumen,'%d-%m-%Y')
	  FROM transaksi_kwitansidetail1 where docentry=sdocentry;
	  DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;
	  set stxtnodokumen='',sbaris=0,slinenum=1;
	  delete from transaksi_kwitansidetail2 where docentry=sdocentry;
	  OPEN cur1;
	  REPEAT
	    FETCH cur1 INTO stgldokumen;
	    IF NOT done THEN
		if sbaris<sbatas then
			SET sbaris=sbaris+1;
			set stxtnodokumen=concat(stxtnodokumen,if(stxtnodokumen='','',' / '),stgldokumen);
		else
			
			insert into transaksi_kwitansidetail2(docentry,linenum,txtnodokumen)
			values (sdocentry,slinenum,trim(stxtnodokumen));
			set sbaris=1;
			SET stxtnodokumen=stgldokumen;
			set slinenum=slinenum+1;
		end if;
		
			
	    END IF;
	  UNTIL done END REPEAT;
	  CLOSE cur1;
	  INSERT INTO transaksi_kwitansidetail2(docentry,linenum,txtnodokumen)
			VALUES (sdocentry,slinenum,trim(stxtnodokumen));
    END */;;
