/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_kartu_stok`(in skodegudang char(6),skodeproduk char(15),sdate date,sget int)
BEGIN
declare syop int;
declare smop int;
DECLARE sday INT;
set syop=year(sdate),smop=month(sdate),sday=day(sdate);
if sget=0 then
	select cast(sdate as date) stockcarddate,yop,mop,nodokumen,tgldokumen,concat(a.kodegudang,'-',mg.namagudang) as namagudang,
	concat(a.kodeproduk,'-',mp.namaproduk) as namaproduk,txtdesc,
	if(SUM(cstock)>0,sum(stock),0) as stockm,
	IF(SUM(cstock)<0,SUM(stock),0) AS stockk,sum(cstock) as cstock,
	p.CmnyName,p.Address1,p.City,p.Faximale,p.Phone1,a.keterangan
	from
	(
	SELECT 	syop AS yop,smop AS mop,'000' AS nodokumen,cast(CONCAT(syop,'-',smop,'-',1) AS date) tgldokumen,mi.kodegudang,mi.kodeproduk,IFNULL(a.stock ,0) AS stock
	,'Saldo Awal' AS txtdesc,IFNULL(a.stock ,0) AS cstock,'' keterangan
	FROM master_inventori mi 
	LEFT JOIN master_stok_balance a ON mi.kodegudang=a.kodegudang AND mi.kodeproduk=a.kodeproduk
	AND yop=IF(smop=1,syop-1,syop) AND mop=IF(smop=1,12,smop-1)
	WHERE if(skodegudang='',mi.kodegudang,skodegudang)=mi.kodegudang and mi.kodeproduk=skodeproduk
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudang,a1.kodeproduk,a1.jumlah AS stock
	,'Pembelian' AS txtdesc,a1.jumlah AS cstock,a.keterangan FROM transaksi_masuk a 
	INNER JOIN transaksi_masuk_detail1 a1 ON a.docentry=a1.docentry
	WHERE YEAR(a.tgldokumen)=syop AND MONTH(a.tgldokumen)=smop AND a1.kodegudang=IF(skodegudang='',a1.kodegudang,skodegudang) 
	and DAY(a.tgldokumen)<=sday and a1.kodeproduk=skodeproduk
	
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudang,a1.kodeproduk,a1.jumlah AS stock
	,'Masuk Lain-lain' AS txtdesc,a1.jumlah AS cstock,a.keterangan FROM transaksi_masuk_lain a 
	INNER JOIN transaksi_masuk_lain_detail1 a1 ON a.docentry=a1.docentry
	WHERE YEAR(a.tgldokumen)=syop AND MONTH(a.tgldokumen)=smop AND a1.kodegudang=IF(skodegudang='',a1.kodegudang,skodegudang)
	AND DAY(a.tgldokumen)<=sday AND a1.kodeproduk=skodeproduk
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudangto,a1.kodeproduk,a1.jumlah AS stock
	,'Transfer Masuk' AS txtdesc,a1.jumlah AS cstock,a.keterangan FROM transaksi_transfer a 
	INNER JOIN transaksi_transfer_detail1 a1 ON a.docentry=a1.docentry
	WHERE YEAR(a.tgldokumen)=syop AND MONTH(a.tgldokumen)=smop AND a1.kodegudangto=IF(skodegudang='',a1.kodegudangto,skodegudang)
	AND DAY(a.tgldokumen)<=sday AND a1.kodeproduk=skodeproduk
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudang,a1.kodeproduk,a1.jumlah AS stock
