/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_loop_kwitansi`(in sdocentry int,slinenum int,
    stxtnodokumen text,stxtnodokumen_no int)
BEGIN
declare ibrs int;
declare ipss int;
declare snodokumen varchar(15);
declare slen_text int;
DECLARE sdetailnum INT;
		
	
	
set slen_text=length(stxtnodokumen);
delete from trnent_gldetail2 where docentry=sdocentry and linenum=slinenum;
set ibrs=1;
set ipss=1;
while ibrs<=stxtnodokumen_no do
	set sdetailnum=ibrs;
	SET ipss= POSITION(  ';' IN stxtnodokumen)-1;
	set snodokumen=mid(stxtnodokumen,1,ipss);
	set stxtnodokumen=mid(stxtnodokumen,ipss+2,slen_text-ipss+2);
	SET slen_text=LENGTH(stxtnodokumen);
	insert into trnent_rabdetail1(docentry,linenum,referensi)
	values
	(sdocentry,ibrs,snodokumen);
	
	INSERT INTO trnent_gldetail2
	(
	docentry,
	linenum,
	detailnum,
	nodokumen,
	audituser,
	auditdate
	)
	VALUES
	(
	sdocentry,
	slinenum,
	sdetailnum,
	snodokumen,
	saudituser,
	NOW()
	);
	
	set ibrs=ibrs+1;
	
end while;	
    END */;;
