/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_master_stok_balance_recalculate`(in syop int,smop int, skodegudang char(6))
BEGIN
	DELETE FROM master_stok_balance WHERE syop=yop AND smop=mop AND IF(skodegudang='',kodegudang,skodegudang)=kodegudang;
	INSERT INTO master_stok_balance
	(yop,mop,kodegudang,kodeproduk,stock,cekstok,objtype,audituser,auditdate)
	SELECT  yop,mop,kodegudang,kodeproduk,SUM(cstock) AS cstock,'N',1,'',NOW()
	FROM
	(
	SELECT 	syop AS yop,smop AS mop,'000' AS nodokumen,CONCAT(syop,'-',smop,'-',1) AS tgldokumen,mi.kodegudang,mi.kodeproduk,IFNULL(a.stock ,0) AS stock
	,'Saldo Awal ' AS txtdesc,IFNULL(a.stock ,0) AS cstock
	FROM master_inventori mi 
	LEFT JOIN master_stok_balance a ON mi.kodegudang=a.kodegudang AND mi.kodeproduk=a.kodeproduk
	AND yop=IF(smop=1,syop-1,syop) AND mop=IF(smop=1,12,smop-1)
	WHERE if(skodegudang='',mi.kodegudang,skodegudang)=mi.kodegudang
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudang,a1.kodeproduk,a1.jumlah AS stock
	,'Pembelian' AS txtdesc,a1.jumlah AS cstock FROM transaksi_masuk a 
	INNER JOIN transaksi_masuk_detail1 a1 ON a.docentry=a1.docentry
	WHERE YEAR(a.tgldokumen)=syop AND MONTH(a.tgldokumen)=smop AND a1.kodegudang=IF(skodegudang='',a1.kodegudang,skodegudang)
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudang,a1.kodeproduk,a1.jumlah AS stock
	,'Masuk Lain-lain' AS txtdesc,a1.jumlah AS cstock FROM transaksi_masuk_lain a 
	INNER JOIN transaksi_masuk_lain_detail1 a1 ON a.docentry=a1.docentry
	WHERE YEAR(a.tgldokumen)=syop AND MONTH(a.tgldokumen)=smop AND a1.kodegudang=IF(skodegudang='',a1.kodegudang,skodegudang)
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudangto,a1.kodeproduk,a1.jumlah AS stock
	,'Transfer Masuk' AS txtdesc,a1.jumlah AS cstock FROM transaksi_transfer a 
	INNER JOIN transaksi_transfer_detail1 a1 ON a.docentry=a1.docentry
	WHERE YEAR(a.tgldokumen)=syop AND MONTH(a.tgldokumen)=smop AND a1.kodegudangto=IF(skodegudang='',a1.kodegudangto,skodegudang)
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudang,a1.kodeproduk,a1.jumlah AS stock
	,'Penjualan' AS txtdesc,a1.jumlah*-1 AS cstock FROM transaksi_keluar a 
	INNER JOIN transaksi_keluar_detail1 a1 ON a.docentry=a1.docentry
	WHERE YEAR(a.tgldokumen)=syop AND MONTH(a.tgldokumen)=smop AND a1.kodegudang=IF(skodegudang='',a1.kodegudang,skodegudang)
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudang,a1.kodeproduk,a1.jumlah AS stock
	,'Keluar Lain-lain' AS txtdesc,a1.jumlah*-1 AS cstock FROM transaksi_keluar_lain a 
	INNER JOIN transaksi_keluar_lain_detail1 a1 ON a.docentry=a1.docentry
	WHERE YEAR(a.tgldokumen)=syop AND MONTH(a.tgldokumen)=smop AND a1.kodegudang=IF(skodegudang='',a1.kodegudang,skodegudang)
	UNION ALL
	SELECT syop,smop,a.nodokumen,a.tgldokumen,a1.kodegudangfr,a1.kodeproduk,a1.jumlah AS stock
	,'Transfer Keluar' AS txtdesc,a1.jumlah*-1 AS cstock FROM transaksi_transfer a 
	INNER JOIN transaksi_transfer_detail1 a1 ON a.docentry=a1.docentry
	WHERE YEAR(a.tgldokumen)=syop AND MONTH(a.tgldokumen)=smop AND a1.kodegudangfr=IF(skodegudang='',a1.kodegudangfr,skodegudang)
	) AS a
	GROUP BY yop,mop,kodegudang,kodeproduk;
    END */;;
DELIMITER ;
