/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_mecah_terbilang`(IN stxtterbilang TEXT,sbatas INT ,sdocentry INT)
BEGIN
	DECLARE schar ,sposisi,slbrtext,sntext,sterbilke INT DEFAULT 1;
	DECLARE sterbilang1,sterbilang2,sterbilang3 VARCHAR(150) DEFAULT '';
	SET slbrtext=LENGTH(stxtterbilang);
	WHILE sntext<slbrtext DO
		IF schar>=sbatas AND MID(stxtterbilang,schar,1)=' ' AND sterbilke=1  THEN
			
			
			SET sterbilke=sterbilke+1;
			SET sterbilang1=LEFT(stxtterbilang,schar);
			SET stxtterbilang=MID(stxtterbilang,schar+1,slbrtext-schar);
			SET schar=0;
			
		END IF;
		IF schar>=sbatas AND MID(stxtterbilang,schar,1)=' ' AND sterbilke=2  THEN
			
			SET sterbilang2=LEFT(stxtterbilang,schar);
			SET sterbilke=sterbilke+1;
			SET stxtterbilang=MID(stxtterbilang,schar+1,slbrtext-schar);
			SET schar=0;
		END IF;
		SET sntext =sntext +1;
		SET schar=schar+1;
	END WHILE;
	IF sterbilke=1  THEN
			SET sterbilang1=stxtterbilang;
	END IF;
	IF sterbilke=2  THEN
			SET sterbilang2=stxtterbilang;
	END IF;
	IF sterbilke=3  THEN
			SET sterbilang3=stxtterbilang;
	END IF;
	/*select sterbilang1,'-',sterbilang2,'-',sterbilang3;*/
	UPDATE transaksi_kwitansi SET 	terbilang1=TRIM(sterbilang1),terbilang2=TRIM(sterbilang2),
					terbilang3=TRIM(sterbilang3)
	WHERE docentry=sdocentry;
    END */;;
DELIMITER ;
