BEGIN
SELECT  IF(skodecustomerfr='',MIN(kodecustomer),skodecustomerfr),
	IF(skodecustomerto='',MAX(kodecustomer),skodecustomerto) INTO skodecustomerfr,skodecustomerto FROM master_customer;
	
