	SELECT ssortby keysortby,
	if(ssortby=1,date_format(a.tgldokumen,'%d-%m-%Y'),a.kodecustomer) sortby1,
	IF(ssortby=1,concat('Tanggal : ',DATE_FORMAT(a.tgldokumen,'%d-%m-%Y')),concat('Customer : ',a.kodecustomer,'-',mc.namacustomer)) sortby2,
	IF(a.totalbayar=0,'N','Y') as txtStsBayar,
	mc.namacustomer,
	a.*,b.nokwitansi,b.tglkwitansi ,p.CmnyName,p.Address1,p.City,p.Faximale,p.Phone1
	FROM transaksi_keluar_tfaktur a
	inner join master_customer mc on mc.kodecustomer=a.kodecustomer
	LEFT JOIN 
	(SELECT k1.nodokumen,GROUP_CONCAT(k.nodokumen) nokwitansi,GROUP_CONCAT(DATE_FORMAT(k.tgldokumen,'%d-%m-%Y')) tglkwitansi FROM transaksi_kwitansidetail1 k1 
	INNER JOIN transaksi_kwitansi k ON k.docentry=k1.docentry
