	WHERE a.tgldokumen BETWEEN stglfr AND stglto AND a.kodecustomer BETWEEN skodecustomerfr AND skodecustomerto
	AND IF(stukarfaktur='A',TRUE,IF(a.totalbayar=0,'N','Y')=stukarfaktur)
	
	ORDER BY IF(ssortby=1,a.tgldokumen,a.kodecustomer),a.nodokumen;
	
end if;
IF sget=1 THEN
	SELECT COUNT(*) FROM transaksi_keluar_tfaktur a
	WHERE a.tgldokumen BETWEEN stglfr AND stglto AND a.kodecustomer BETWEEN skodecustomerfr AND skodecustomerto
