	END IF;
	
	if sawal=1 then
	
		set sreferensi='Transfer TBP ke GL';
		SET sketerangan='Auto Proses'; 
		
		call spinsert_trnent_gl(sdocentry,stanggal,syop,smop,sgr_dataentry,snotran_crsrkey,sreferensi,
		sketerangan,sjumlah_crsr,sjumlah_crsr,sproses,sglstatus,saudituser);
		set sawal=0;
	else
		update trnent_gl set jumtotdebet=jumtotdebet+sjumlah_crsr,jumtotkredit=jumtotkredit+sjumlah_crsr
		where docentry=sdocentry;
	end if;
	
	set sreferensi2=concat('TBP Periode ',syop_crsr,smop_crsr);
	SET sketerangan2=if(sjnsbayar_crsr=1,'Tunai','Transfer');
	set slinenum=slinenum+1;
	call spinsert_trnent_gldetail1 (sdocentry,slinenum,scoa_debet,sjumlah_crsr,0,sreferensi2,sketerangan2,
	sbasenoslip,sbaseentry,sbaseline,sproses,saudituser);
	SET slinenum=slinenum+1;
	CALL spinsert_trnent_gldetail1 (sdocentry,slinenum,scoa_kredit,0,sjumlah_crsr,sreferensi2,sketerangan2,
	sbasenoslip,sbaseentry,sbaseline,sproses2,saudituser);
    END IF;     
  UNTIL done END REPEAT;
  CLOSE cur1;
  
	update transaksi_kwitansi a ,
	(
	SELECT a.docentry,b.yop,b.mop,b.jnsbayar,a.totalstlpfaktur AS jumlah FROM transaksi_kwitansi a INNER JOIN
	transaksi_kwitansi_temp b ON YEAR(a.tglkwitansi)=b.yop AND MONTH(a.tglkwitansi)=b.mop AND a.jnsbayar=b.jnsbayar 
	AND a.dokstatus='O' AND b.audituser=saudituser
	) as b 
	set a.dokstatus='C'
