/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_proses_master_perpindahan_siswa`(in syop int,smop int,saudituser varchar(10))
BEGIN
declare syopcount,smopcount int;
set syopcount=syop,smopcount=smop;
while syopcount<=year(now()) do
	
	WHILE smopcount<=12 DO
	
	delete from master_perpindahan_siswa where yop=syop and mop=smop;
	INSERT INTO master_perpindahan_siswa
	(	yop,mop,siswabulanlalu,siswabaru,pelajarantambahan,pindahmasuk, 
		masukkembali,jumlahtambah,libur,keluar,pindahkeluar,lainlain, 
		jumlahkurang,siswabulanini,auditdate,audituser
	)
	SELECT 	yop,mop,
		SUM(siswabulanlalu) AS siswabulanlalu,
		SUM(siswabaru) AS siswabaru,SUM(peltambahan) AS peltambahan,SUM(pindahmasuk) AS pindahmasuk,
		SUM(masukkembali) AS masukkembali,SUM(jumlahtambah) AS jumlahtambah,
		SUM(libur) AS libur,SUM(keluar) AS keluar,
		SUM(pindahkeluar) AS pindahkeluar,SUM(lainlain) AS lainlain,
		SUM(jumlahkurang) AS jumlahkurang,SUM(siswabulanini) AS siswabulanini,
		now(),saudituser
	FROM
	(
	SELECT YEAR(IF(masukkembali='1',konfirmasitglmasuk,tanggal)) AS yop ,MONTH(IF(masukkembali='1',konfirmasitglmasuk,tanggal)) AS mop,
	0 AS siswabulanlalu,0 AS siswabaru,0 AS pindahmasuk,0 AS peltambahan,
	SUM(IF(masukkembali='1',1,0)) AS masukkembali,0 AS jumlahtambah,
	SUM(IF(masukkembali='0',1,0)) AS libur,0 AS keluar,0 pindahkeluar,0 AS lainlain,0 AS jumlahkurang,0 AS siswabulanini
	FROM transaksi_cuti tc
	WHERE YEAR(IF(masukkembali='1',konfirmasitglmasuk,tanggal)) = syop AND MONTH(IF(masukkembali='1',konfirmasitglmasuk,tanggal))=smop
		and masukkembali='1' and 
		(SELECT COUNT(nodokumen) FROM transaksi_cuti_detail1  WHERE tc.nodokumen=nodokumen AND NOT stscutibase=1)=0
	GROUP BY YEAR(IF(masukkembali='1',konfirmasitglmasuk,tanggal))  ,MONTH(IF(masukkembali='1',konfirmasitglmasuk,tanggal))
	UNION ALL
	
	SELECT YEAR(tanggal) AS yop ,MONTH(tanggal) AS mop,
	0 AS siswabulanlalu,0 AS siswabaru,0 AS pindahmasuk,0 AS peltambahan,
	0 AS masukkembali,0 AS jumlahtambah,
	1 AS libur,0 AS keluar,0 pindahkeluar,0 AS lainlain,0 AS jumlahkurang,0 AS siswabulanini
	FROM transaksi_cuti tc
		WHERE YEAR(tanggal) = syop AND MONTH(tanggal)=smop AND 
		(SELECT COUNT(nodokumen) FROM transaksi_cuti_detail1  WHERE tc.nodokumen=nodokumen AND NOT stscutibase=1)=0
	GROUP BY YEAR(tanggal)  ,MONTH(tanggal)
	UNION ALL
	
	SELECT
	  YEAR(tglpendaftaran) AS yop, MONTH(tglpendaftaran) AS mop,0 AS siswabulanlalu,
	  SUM(IF((penggolongan = '1'),1,0)) AS siswabaru,
	  SUM(IF((penggolongan = '2'),1,0)) AS pindahmasuk,
	  SUM(IF((penggolongan = '3'),1,0)) AS peltambahan,
	  0 AS masukkembali,0 AS jumlahtambah,0 AS libur,0 AS keluar,0 pindahkeluar,0 AS lainlain,0 AS jumlahkurang,0 AS siswabulanini
	FROM transaksi_pendaftaran
	WHERE YEAR(tglpendaftaran)=syop AND  MONTH(tglpendaftaran)=smop
	GROUP BY  YEAR(tglpendaftaran) , MONTH(tglpendaftaran)
	UNION ALL
	SELECT YEAR(tgldokumen) AS yop ,MONTH(tgldokumen) AS mop,0 AS siswabulanlalu,
	0 AS siswabaru,0 AS pindahmasuk,0 AS peltambahan,0 AS masukkembali,0 AS jumlahtambah,0 AS libur,
	SUM(IF(kodekeluar='1',1,0)) AS keluar,
	SUM(IF(kodekeluar='2',1,0)) AS pindahkeluar,
	SUM(IF(kodekeluar='3',1,0)) AS lainlain,0 AS jumlahkurang,0 AS siswabulanini
	FROM transaksi_siswa_keluar
	WHERE YEAR(tgldokumen)=syop AND MONTH(tgldokumen)=smop
	GROUP BY YEAR(tgldokumen) ,MONTH(tgldokumen) 
	UNION ALL
	SELECT syop,smop,siswabulanini AS siswabulanlalu,0 AS siswabaru, 
	0 AS pelajarantambahan,0 AS pindahmasuk,0 AS masukkembali,0 AS jumlahtambah,0 AS libur,0 AS keluar,0 AS pindahkeluar,0 AS lainlain,0 AS jumlahkurang,0 AS siswabulanini
	FROM master_perpindahan_siswa WHERE yop=IF(smop=1,syop-1,syop) AND mop=IF(smop=1,12,smop-1)
	) AS a
	GROUP BY yop,mop;
	
	update master_perpindahan_siswa
	set 	jumlahtambah=siswabaru+pelajarantambahan+pindahmasuk+masukkembali,
		jumlahkurang=libur+keluar+pindahkeluar+lainlain,
		siswabulanini=siswabulanlalu+jumlahtambah-jumlahkurang
	WHERE yop=syop AND mop=smop;
	
	set smopcount=smopcount+1;
	set smop=smopcount;
	END WHILE;
	SET smopcount=1;
	SET smop=smopcount;
	set syopcount=syopcount+1;
	SET syop=syopcount;
end while;
	
END */;;
DELIMITER ;
