/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 PROCEDURE `sp_proses_rab_to_gl`(IN ssessiondate char(10),snotran VARCHAR(15),saudituser varchar(10))
BEGIN
  /* call sp_atest(2011,11,1,'01') */  
  DECLARE done INT DEFAULT 0;
  DECLARE Clicks INT;
  
  declare syop_crsr int;
  declare smop_crsr int;
  declare snoslip_crsr varchar(15);
  DECLARE snoslip_crsrkey VARCHAR(15) default '';
  DECLARE sreferensi_crsr VARCHAR(40);
  DECLARE sketerangan_crsr VARCHAR(40);
  declare sjumlahtotal_crsr double;
  DECLARE scoa_crsr   VARCHAR(14);
  DECLARE sjumdebet_crsr DOUBLE;
  DECLARE sjumcredit_crsr DOUBLE;
  declare snotran_crsr VARCHAR(15);
  DECLARE snotran_crsrkey VARCHAR(15) default '';
   
	DECLARE sdocentry INT(11);
	DECLARE stanggal DATE;
	DECLARE syop INT(2);
	DECLARE smop INT(2);
	DECLARE sgr_dataentry VARCHAR(3);
	DECLARE sreferensi VARCHAR(40) default '';
	DECLARE sketerangan VARCHAR(40) DEFAULT '';
	DECLARE sjumtotdebet DECIMAL(15,2) default 0;
	DECLARE sjumtotkredit DECIMAL(15,2) DEFAULT 0;
	DECLARE sproses CHAR(1);
	DECLARE sglstatus CHAR(1);
	
	declare slinenum INT(11) default 0;
	declare scoa VARCHAR(14);
	declare sjumdebet DECIMAL(15,2);
