	declare sketerangan2 VARCHAR(40);
	declare sbasenoslip VARCHAR(15);
	declare sbaseentry INT(11);
	declare sbaseline INT(11);
	declare sproses2 CHAR(1);
	declare scoa_debet VARCHAR(14);
	declare scoa_kredit VARCHAR(14);
	declare speriodekey DOUBLE default 0;
	
	DECLARE sawal  int default 1 ;
	
  DECLARE cur1 CURSOR FOR 
        SELECT 	a.yop, a.mop, a.noslip, a.referensi, a.keterangan, a.jumlahtotal,a.coa,SUM(jumdebet) AS jumdebet,SUM(jumcredit) AS jumcredit,
		a.notran
	FROM
	(
	SELECT 	a.yop, 
		a.mop, a.noslip, a.referensi, a.keterangan, a.jumlahtotal,
		a1.coa_debet AS coa,a1.jumlah AS jumdebet,0 AS jumcredit,b.notran
	FROM 	trnent_rab a 
		INNER JOIN trnent_rabdetail1 a1 ON a.docentry=a1.docentry AND a.closed='N' AND a.jumlahtotal<>0
		INNER JOIN transaksi_kwitansi_temp b ON a.yop=b.yop AND a.mop=b.mop AND b.audituser=saudituser
	UNION ALL
	SELECT 	a.yop, 
		a.mop, a.noslip, a.referensi, a.keterangan, a.jumlahtotal,
		a1.coa_kredit AS coa,0 AS jumdebet,a1.jumlah AS jumcredit,b.notran
	FROM 	trnent_rab a 
		INNER JOIN trnent_rabdetail1 a1 ON a.docentry=a1.docentry AND a.closed='N' AND a.jumlahtotal<>0
		INNER JOIN transaksi_kwitansi_temp b ON a.yop=b.yop AND a.mop=b.mop AND b.audituser=saudituser
	) AS a
	GROUP BY a.yop, a.mop, a.noslip, a.referensi, a.keterangan, a.jumlahtotal,a.notran,a.coa
	order by a.yop, a.mop, a.noslip, a.referensi, a.keterangan, a.jumlahtotal,a.notran,a.coa desc;
  DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;
  
  select ifnull(max(docentry),0) into sdocentry from trnent_gl;
  set syop=year(ssessiondate);
  SET smop=month(ssessiondate);
  set stanggal=ssessiondate;
  OPEN cur1;
  REPEAT
  FETCH cur1 INTO  syop_crsr,smop_crsr ,snoslip_crsr,sreferensi_crsr,sketerangan_crsr,
  sjumlahtotal_crsr,scoa_crsr,sjumdebet_crsr,sjumcredit_crsr,snotran_crsr;
    IF NOT done THEN
	/* check gl account balance */
	
	if snotran_crsrkey<>snotran_crsr then
	   set snoslip_crsrkey=snoslip_crsr;
	   set snotran_crsrkey=snotran_crsr;
	   set sdocentry=sdocentry+1;
		SELECT 	gr_dataentry INTO sgr_dataentry FROM 
