		set sreferensi='Transfer RAB ke GL';
		SET sketerangan='Auto Proses'; 
		
		call spinsert_trnent_gl(sdocentry,stanggal,syop_crsr,smop_crsr,sgr_dataentry,snotran_crsr,sreferensi,
		sketerangan,sjumlahtotal_crsr,sjumlahtotal_crsr,sproses,sglstatus,saudituser);
		set sawal=0;
	else 
		if snoslip_crsrkey<>snoslip_crsr then
			SET snoslip_crsrkey=snoslip_crsr;
			update trnent_gl set 	jumtotdebet=jumtotdebet+sjumlahtotal_crsr,
						jumtotkredit=jumtotkredit+sjumlahtotal_crsr
			where docentry=sdocentry;
		end if;
	end if;
	
	set sreferensi2=concat(snoslip_crsr,'/',sreferensi_crsr);
	SET sketerangan2=sketerangan_crsr;
	set slinenum=slinenum+1;
	call spinsert_trnent_gldetail1 (sdocentry,slinenum,scoa_crsr,sjumdebet_crsr,sjumcredit_crsr,sreferensi2,sketerangan2,
	sbasenoslip,sbaseentry,sbaseline,sproses,saudituser);
	
    END IF;     
  UNTIL done END REPEAT;
  CLOSE cur1;
  
	update trnent_rab a ,
	(
	SELECT 	a.docentry
	FROM 	trnent_rab a 
		INNER JOIN transaksi_kwitansi_temp b ON a.yop=b.yop AND a.mop=b.mop AND b.audituser=saudituser
		AND a.closed='N' AND a.jumlahtotal<>0
	) as b 
	set a.closed='Y'
	where a.docentry=b.docentry;
  END */;;
