/*!50003 CREATE*/ /*!50020 DEFINER=`sa`@`%`*/ /*!50003 PROCEDURE `sp_setting_import_trx_apinvoice_dari_accpac`(in syop integer ,smop integer)
BEGIN
	UPDATE ap_optionaldetail1 SET docnum=1 WHERE docnumid=1;
