		JRNLDR AS jrnldebit,JRNLCR AS jrnlcredit, JRNLQTY AS jrnlquantity,
		NOW() AS entrydate,NOW() AS auditdate,'Admin' AS audituser,1 AS objtype,'Accapp' AS cmpnyid
	FROM
	(		
	SELECT 	BATCHID,BTCHENTRY,AUDTDATE,AUDTTIME,AUDTUSER, AUDTORG, SRCELEDGER, SRCETYPE, FSCSYR, 
		FSCSPERD, SWEDIT, SWREVERSE, JRNLDESC, JRNLDR, JRNLCR, JRNLQTY, DATEENTRY, DRILSRCTY, DRILLDWNLK, 
		DRILAPP
		 
		FROM 
		data_accpac.gljeh  WHERE FSCSYR=syop AND FSCSPERD=smop AND SRCELEDGER='GL' AND SRCETYPE='JE'
		ORDER BY  BATCHID,BTCHENTRY
		) AS a ,
				(SELECT @btchentry:=(SELECT docnum FROM gl_optiondetail1 WHERE modulid=1)-1) AS btchentry, 
				(SELECT @btchentrykey:=-1) AS btchentrykey
	) AS a
	;
	INSERT INTO gl_batchlist 
	(
		batchid,readytpost,btchdesc,srceledgr,batchdate,batchtype,batchsts,postngseq,postdate, 
		debittot,credittot,qtytotal,entrycnt,nextentry, 
		entrydate,auditdate,audituser,objtype,cmpnyid
	)
	SELECT
		batchid,readytpost,btchdesc,srceledgr,batchdate,batchtype,batchsts,postngseq,postdate, 
		debittot,credittot,qtytotal,entrycnt,nextentry, 
		entrydate,auditdate,audituser,objtype,cmpnyid
	FROM
	(
	SELECT
		@btchentry:=@btchentry+1 AS batchid,1 AS readytpost,CONCAT(BTCHDESC,' Reff.no ',BATCHID) AS btchdesc,
		'GL' AS srceledgr,DATECREAT AS batchdate,BATCHTYPE AS batchtype,'1' AS batchsts,0 AS postngseq,
		NULL AS postdate, 
		DEBITTOT AS debittot,CREDITTOT AS credittot,QTYTOTAL AS qtytotal,ENTRYCNT AS entrycnt,NEXTENTRY AS nextentry, 
		NOW() AS entrydate,NOW() AS auditdate,'Admin' AS audituser,1 AS objtype,'Admin' AS cmpnyid
	FROM
	(
	SELECT 	a.BATCHID,a.AUDTDATE, a.AUDTTIME, a.AUDTUSER, a.AUDTORG, ACTIVESW,BTCHDESC, SRCELEDGR,DATECREAT, 
		DATEEDIT,BATCHTYPE, BATCHSTAT, POSTNGSEQ, DEBITTOT, CREDITTOT, QTYTOTAL, ENTRYCNT, NEXTENTRY, 
		ERRORCNT, ORIGSTATUS, SWPRINTED
		 
		FROM 
		data_accpac.glbctl a INNER JOIN data_accpac.gljeh b ON a.BATCHID=b.BATCHID 
		WHERE b.FSCSYR=syop AND b.FSCSPERD=smop AND b.SRCELEDGER='GL' AND b.SRCETYPE='JE'
		GROUP BY a.BATCHID
		ORDER BY a.BATCHID
	) AS a,	(SELECT @btchentry:=(SELECT docnum FROM gl_optiondetail1 WHERE modulid=1)-1) AS btchentry
	) AS a;
	
	update gl_optiondetail1 set  docnum = ( select max(batchid) from gl_batchlist )+1 where  modulid=1;
    END */;;
