/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `sp_transaksi_by_produk_rpt`(in 
stransid int,
stgldokumenfr date,stgldokumento date,
soption int,skodeprodukfr char(15),skodeprodukto char(15),
scustoption INT,skodecustmrfr CHAR(15),skodecustmrto CHAR(15),
sget int)
BEGIN
/*
CALL sp_transaksi_by_produk_rpt(1,'2012-11-01','2012-11-29',1,'','',2,'','',0)
*/
select  if(skodeprodukfr='',min(IF(soption=1,mp.kodebrand,IF(soption=2,mp.kodekategori,mp.kodefungsi))),skodeprodukfr),
	IF(skodeprodukto='',max(IF(soption=1,mp.kodebrand,IF(soption=2,mp.kodekategori,mp.kodefungsi))),skodeprodukto)
	into
	skodeprodukfr ,skodeprodukto
from master_produk mp;
if scustoption=2 or scustoption=4 then
	select if(skodecustmrfr='',min(kodecustomer),skodecustmrfr),IF(skodecustmrto='',max(kodecustomer),skodecustmrto)
	into skodecustmrfr,skodecustmrto from master_customer;
end if;
IF scustoption=1 OR scustoption=3 THEN
	SELECT IF(skodecustmrfr='',MIN(kodecustomer),skodecustmrfr),IF(skodecustmrto='',MAX(kodecustomer),skodecustmrto)
	INTO skodecustmrfr,skodecustmrto FROM master_supplier;
END IF;
if sget=0 then
	select 
	a.kodecustomer,a.namacustomer,
	IF(soption=1,a.kodebrand,IF(soption=2,a.kodekategori,a.kodefungsi)) as sort1,
	IF(soption=1,concat(a.kodebrand,'-',mb.namabrand),IF(soption=2,concat(a.kodekategori,'-',mk.namakategori),CONCAT(a.kodefungsi,',',mf.namafungsi))) AS sortdesc,
	a.transid,a.tgldokumen,a.kodeproduk,a.namaproduk,a.kodegudang,
		a.kodebrand,a.kodekategori,a.kodefungsi,
		mb.namabrand,mk.namakategori,mf.namafungsi,
	SUM(a.jumlah) AS jumlah,SUM(a.feetotal) AS feetotal, SUM(a.diskontotal) AS diskontotal,
	SUM(a.totalsebdiskon) AS totalsebdiskon,SUM(a.totalsetdiskon) AS totalsetdiskon,p.CmnyName,p.Address1,p.City,p.Faximale,p.Phone1
	from
	(
	SELECT 	1 AS transid,tgldokumen,b.kodeproduk,mp.namaproduk,b.kodegudang,
		mp.kodebrand,mp.kodekategori,mp.kodefungsi,
		a.kodecustomer,ms.namacustomer,
	SUM(jumlah) AS jumlah,SUM(b.fee*jumlah) AS feetotal, SUM(diskontotal) AS diskontotal,SUM(totalsebdiskon) AS totalsebdiskon,SUM(totalsetdiskon) AS totalsetdiskon
	FROM 	transaksi_masuk a INNER JOIN transaksi_masuk_detail1 b ON a.docentry=b.docentry
	inner join master_produk mp on mp.kodeproduk=b.kodeproduk
	inner join master_supplier ms on ms.kodecustomer=a.kodecustomer
	where if(soption=1,mp.kodebrand,IF(soption=2,mp.kodekategori,mp.kodefungsi)) between skodeprodukfr and skodeprodukto
	and tgldokumen between stgldokumenfr and stgldokumento and if(stransid=1,true,false) 
	and a.kodecustomer between skodecustmrfr and skodecustmrto
	GROUP BY tgldokumen,b.kodeproduk,b.kodegudang,a.kodecustomer
