	sdocentry INT(11),
	slinenum INT(11),
	skodeproduk VARCHAR(15),
	skodeharga VARCHAR(6),
	skodediskon VARCHAR(6),
	sharga DECIMAL(13,2),
	sjumlah DECIMAL(13,2),
