	SET slen_text=LENGTH(stxtnodokumen);
	
	INSERT INTO trnent_gldetail2
	(
	docentry,
	linenum,
	detailnum,
	nodokumen,
	audituser,
	auditdate
	)
	VALUES
	(
	sdocentry,
	slinenum,
	sdetailnum,
	snodokumen,
	saudituser,
	NOW()
	);
	update transaksi_kwitansi set dokstatus=0 where nodokumen=snodokumen;
	set ibrs=ibrs+1;
	
end while;	
    END */;;
