	  DECLARE done INT DEFAULT 0;
	  DECLARE stextheader,stextformulation CHAR(100);
	  DECLARE cur1 CURSOR FOR SELECT textheader,textformulation 
	  FROM setting_financial_rpt_detail1 where docentry=7;
